import { useState } from 'react';
import './App.css';
const uuid=require('uuid');
function App() {
  const [image,setImage]=useState('');
  const [uploadResultMessage,setUploadResultMessage]=useState('Please upload an image');
  const [visitorName, setVisitorName]=useState('placeholder.jpeg');


  function sendImage(e){
    e.preventDefault();
    setVisitorName(image.name);
    const visitorImageName=uuid.v4();
    fetch(`https://xupsvshql3.execute-api.us-east-1.amazonaws.com/prod/visitors646/${visitorImageName}.jpeg`,{
      method: 'PUT'
    });

  }

  return (
    <div className="App">
      <h2>Facial Recognition System</h2>
      <form onSubmit={sendImage}>
        <input type='file' name='image' onChange={e=> setImage(e.target.files[0])}/>
          <button type='submit' >Authenticate</button>
      </form>
      <div>{uploadResultMessage}</div>
      <img src={ require(`./visitors/${visitorName}`)} alt="Visitor" height={250} width={250}/>
    </div>
  );
}

export default App;
